import java.util.*;public class a {	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		int T = in.nextInt();		for(int t=1; t<=T; t++) {			int a = in.nextInt();			int b = in.nextInt();			double best = b+2;			double crnt = 1;			for(int i=0; i<a; i++) {				crnt *= in.nextDouble();				double pOk = crnt;				double pBad = 1-crnt;				int deleted = a-i-1;				double expOk = pOk* (deleted + b-i);				double expBad = pBad* (deleted + b-i + b + 1);				best = Math.min(best, expOk + expBad);			}			System.out.printf("Case #%d: %.9f%n", t,best);		}	}}