import java.io.*;import java.util.*;class Password {	public static void main(String [] args) throws Exception{		BufferedReader in = new BufferedReader(new InputStreamReader(System.in));		int T = Integer.parseInt(in.readLine());		for(int i = 0; i < T; i++){			StringTokenizer st = new StringTokenizer(in.readLine());			int A = Integer.parseInt(st.nextToken());			int B = Integer.parseInt(st.nextToken());			st = new StringTokenizer(in.readLine());			double[] error = new double[A];			for(int j = 0; j < A; j++){				error[j] = Double.parseDouble(st.nextToken());			}			double[] pCorrect = new double[A+1];			pCorrect[0] = 1;			for(int j = 1; j <= A; j++){				pCorrect[j] = pCorrect[j-1]*error[j-1];			}			double E1 = pCorrect[A]*(B-A+1) + (1-pCorrect[A])*(2*B-A+2);			double E3 = 2 + B;			double[] E2 = new double[A+1];			for(int j = 1; j <= A; j++){				E2[j] = pCorrect[A-j]*(B-A+2*j+1) + (1-pCorrect[A-j])*(B-A+2*j + 2 + B);			}			double ans = Math.min(E1,E3);;			for(int j = 1; j <=A; j++){				if(E2[j] < ans) ans = E2[j];			}			System.out.println("Case #" + (i+1) + ": " + ans);		}	}}