import java.io.File;import java.io.FileNotFoundException;import java.io.PrintWriter;import java.util.Scanner;import java.util.Set;import java.util.HashSet;class A {	public static void main(String[] args) throws FileNotFoundException {		Scanner in = new Scanner(new File(A.class.getSimpleName() + ".in"));        PrintWriter out = new PrintWriter(new File(A.class.getSimpleName() + ".out"));				int tt = in.nextInt();		for(int t=1; t<=tt; ++t) {                        			long p = in.nextLong();			long q = in.nextLong();								long o;						long pp=p, qq=q;			while(true) {				o=qq%pp;				if(o==0) {					break;				} else {					qq=pp;					pp=o;				}			}			o = pp;			pp = p/o;			qq = q/o;						boolean flag=false;			long e=1;			int i;			for(i=0; i<=40; ++i) {				if(e==qq) {					flag = true;					break;				}				e = e*2;			}			if(!flag) {				out.println("Case #"+t+": impossible");			} else {				e=1;				int j, k=0;				for(j=0; j<=40; ++j) {					if(pp>e) {												k=j;					}					e = e*2;				}				int a = i-k;				out.println("Case #"+t+": "+a);			}						        }				out.close();	}}